import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getFiles, FileItem } from '../api/fileApi';

const FileExplorerPage: React.FC = () => {
    const [files, setFiles] = useState<FileItem[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);
    const { '*': pathParam } = useParams();
    const navigate = useNavigate();
    // Sanitize path from URL parameters
    const currentPath = pathParam
        ? pathParam.replace(/^\/+|\/+$/g, '').replace(/\.\./g, '')
        : '';

    useEffect(() => {
        const fetchFiles = async () => {
            try {
                setLoading(true);
                setError(null);
                const data = await getFiles(currentPath);
                setFiles(data);
            } catch (err) {
                const message = err instanceof Error ? err.message : 'Unknown error';
                console.error('Fetch error:', err);
                setError(`Failed to load files: ${message}`);
            } finally {
                setLoading(false);
            }
        };

        fetchFiles();
    }, [currentPath]);

    const handleDirectoryClick = (file: FileItem) => {
        if (file.type === 'directory') {
            // Navigate to the subdirectory
            const newPath = currentPath ? `${currentPath}/${file.name}` : file.name;
            navigate(`/files/${newPath}`);
        }
    };

    if (loading) {
        return <div className="loading">Loading files...</div>;
    }

    if (error) {
        return (
            <div className="error">
                <h3>Error</h3>
                <p>{error}</p>
                <button onClick={() => window.location.reload()}>Retry</button>
            </div>
        );
    }

    return (
        <div className="file-explorer">
            <h2>File Explorer: {currentPath || 'Root'}</h2>
            {files.length === 0 ? (
                <p>No files found</p>
            ) : (
                <ul className="file-list">
                    {files.map((file) => (
                        <li
                            key={file.name}
                            className={file.type}
                            onClick={() => handleDirectoryClick(file)}
                            style={{ cursor: file.type === 'directory' ? 'pointer' : 'default' }}
                        >
                            {file.type === 'directory' ? 'üìÅ' : 'üìÑ'}
                            <span className="file-name">{file.name}</span>
                            <span className="file-size">{file.size} bytes</span>
                            <span className="file-modified">
                                {new Date(file.modified).toLocaleString()}
                            </span>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default FileExplorerPage;
