import axios from 'axios';

export interface FileItem {
    name: string;
    type: 'file' | 'directory';
    size: number;
    modified: string;
}

export const getFiles = (path: string) => {
    return axios.get<FileItem[]>('/api/files', {
        params: { path },
        timeout: 5000
    });
};
